import React from 'react'
import SystemDisplay from '../components/SystemDisplay'
import { baseSystem } from '../classes/System'

class System extends React.Component {

    state = {
        system: baseSystem
    }

    componentDidMount() {
        this.interval = setInterval(() => {
            const nextSystem = this.state.system.step()
            this.setState({
                ...this.state,
                system: nextSystem
            }, () => {this.forceUpdate()})
        }, 5)
    }

    componentWillUnmount() {
        clearInterval(this.interval)
    }

    render() {
        return (
            <SystemDisplay system={this.state.system} />
        )
    }

}

export default System
