import React from 'react'

const EntityDisplay = ({ entity }) => {

    const dimensions = `${entity.radius * 2}px`;
    const [x, y] = entity.position
    const style = {
        height: dimensions,
        width: dimensions,
        backgroundColor: entity.color,
        borderRadius: "50%",
        left: `${x - entity.radius}px`,
        bottom: `${y - entity.radius}px`,
    }

    return (
        <div className="entity planet" style={style}></div>
    )

}

export default EntityDisplay
