import React from 'react'

const EntityDisplay = props => {

    const entity = props.entity

    const [x, y] = entity.position

    const style = {
        height: `${entity.radius * 2}px`,
        width: `${entity.radius * 2}px`,
        backgroundColor: entity.color,
        borderRadius: "50%",
        left: `${x - entity.radius}px`,
        bottom: `${y - entity.radius}px`,
        // borderBottom: `15px solid ${entity.color}`
    }

    return (
        <div className="entity ship" style={style}></div>
    )
}

export default EntityDisplay
