import React, { useState, useEffect } from 'react'
import EntityComponent from './EntityComponent'
import { baseSystem } from '../classes/System'

const SystemComponent = ({ system, displayRadius }) => {

    const planetStyles = system.planets.map(planet => planet.style())

    const zoom = system.radius / displayRadius

    const entityComponents = system.planets.map((entity, index) => (
        <EntityComponent key={index} entity={entity} zoom={zoom} />
    ))

    const style = {
        width: `${displayRadius}px`,
        height: `${displayRadius}px`,
    }

    return (
        <div className="space" style={style}>
            {entityComponents}
        </div>
    )
}

export default SystemComponent
