class Orbit {
    constructor(primary, radius, period, initialAngle, rotation = 0, direction = 1) {
        this.primary = primary
        this.radius = radius
        this.period = period
        this.initialAngle = initialAngle * Math.PI / 180
        this.rotation = rotation * Math.PI / 180
        this.direction = direction
    }

    position(steps) {
        const [minorRadius, majorRadius] = Array.isArray(this.radius) ? this.radius.sort() : [this.radius, this.radius];
        const [centreX, centreY] = this.getCentre(majorRadius, minorRadius)
        const angle = 2 * Math.PI * this.direction / this.period * steps + this.initialAngle;
        const [cosAngle, sinAngle] = [Math.sin(angle), Math.cos(angle)];
        const [cosRotation, sinRotation] = [Math.sin(this.rotation), Math.cos(this.rotation)]
        const term1 = majorRadius * cosAngle * cosRotation
        const term2 = minorRadius * sinAngle * sinRotation
        const x = term1 - term2 + centreX
        const y = term1 + term2 + centreY

        // const x = majorRadius * Math.cos(angle) + centreX
        // const y = minorRadius * Math.sin(angle) + centreY

        return [x, y]
    }

    // unrotated_position(steps) {
    //     const [radiusX, radiusY] = Array.isArray(this.radius) ? this.radius : [this.radius, this.radius];
    //     const [centreX, centreY] = this.getCentre(radiusX, radiusY)
