class Orbit {
    constructor(primary, radius, period, initial, direction = 1, focus = 1) {
        this.primary = primary
        this.radius = radius
        this.period = period
        this.initial = initial
        this.direction = direction
    }

    position(steps) {
        const [primaryX, primaryY] = Array.isArray(this.primary) ? this.primary : this.primary.position;
        const [radiusX, radiusY] = Array.isArray(this.radius) ? this.radius : [this.radius, this.radius];

        let centreX, centreY

        if (radiusX === radiusY) {
            [centreX, centreY] = [primaryX, primaryY]
        } else if (radiusX > radiusY)  {
            centreY = primaryY
            centreX = primaryX + this.focus * (radiusX ** 2 - radiusY ** 2) ** 0.5
        } else {
            centreX = primaryX
            centreY = primaryY + this.focus * (radiusY ** 2 - radiusX ** 2) ** 0.5
        }

        const angle = this.direction * 2 * Math.PI / this.period * steps + this.initial * Math.PI / 180

        const radius = this.radius

        const x = centreX + radiusX * Math.cos(angle)
        const y = centreY + radiusY * Math.sin(angle)
        return [x, y]
    }
}

export default Orbit
