class Planet {

    constructor(system, color, radius, x = nil, y = nil) {
        this.system = system
        this.color = color
        this.radius = radius
        this.position = [x, y]
    }

    set position(position) {
        [this.x, this.y] = position
    }

    get position() {
        return [this.x, this.y]
    }

    step() {
        const steps = this.system.steps
        if (this.orbit) {
            this.position = this.orbit.position(steps)
        } 
    }

    style() {
        const dimensions = `${this.radius * 2}px`;
        const [x, y] = this.position
        return {
            height: dimensions,
            width: dimensions,
            backgroundColor: this.color,
            borderRadius: "50%",
            left: `${x - this.radius}px`,
            bottom: `${y - this.radius}px`,
        }
    }

}

export default Planet
