import React, { useState, useEffect } from 'react'
import EntityDisplay from './EntityDisplay'
import { baseSystem } from '../classes/System'

const SystemDisplay = ({ system }) => {

    const planetStyles = baseSystem.planets.map(planet => planet.style())

    const styles = planetStyles.map((style, index) => (
        <EntityDisplay key={index} style={style} />
    ))

    return (
        <div className="space">
            {styles}
        </div>
    )
}

export default SystemDisplay
