class PlanetStyle {

    constructor(radius, color) {
        this.radius = radius
        const dimensions = `${radius * 2}px`
        this.height = dimensions
        this.width = dimensions
        this.backgroundColor = color
    }

    set position(position) {
        console.log('style')
        console.log(position);
        const [x, y] = position
        this.left = `${x - this.radius}px`
        this.bottom = `${y - this.radius}px`
        console.log(this.radius)
    }
}

export default PlanetStyle
