class Planet {

    constructor(system, color, radius, mass, x = null, y = null) {
        this.system = system
        this.mass = mass
        this.color = color
        this.radius = radius
        this.position = [x, y]
        this.previousPosition = [x, y]
    }

    set position(position) {
        [this.x, this.y] = position
    }

    get position() {
        return [this.x, this.y]
    }

    set previousPosition(position) {
        [this.previousX, this.previousY] = position
    }

    get previousPosition() {
        return [this.previousX, this.previousY]
    }

    get velocity() {
        return [this.x - this.previousX, this.y - this.previousY]
    }

    step() {
        const steps = this.system.steps
        this.previousPosition = this.position

        if (this.orbit) {
            this.position = this.orbit.position(steps)
        } 
    }

}

export default Planet
