class Planet {

    constructor(system, color, radius, mass, x = null, y = null) {
        this.system = system
        this.mass = mass
        this.color = color
        this.radius = radius
        this.position = [x, y]
    }

    set position(position) {
        [this.x, this.y] = position
    }

    get position() {
        return [this.x, this.y]
    }

    step() {
        const steps = this.system.steps
        if (this.orbit) {
            this.position = this.orbit.position(steps)
        } 
    }

}

export default Planet
