import React from 'react'

const PlanetComponent = ({ entity, zoom }) => {

    const dimensions = `${entity.radius * zoom * 2}px`;
    let [x, y] = entity.position
    x *= zoom
    y *= zoom

    // const style = {
    //     height: dimensions,
    //     width: dimensions,
    //     backgroundColor: entity.color,
    //     borderRadius: "50%",
    //     left: `${x - entity.radius}px`,
    //     bottom: `${y - entity.radius}px`,
    // }
   const style = {
        width: 0,
        height: 0,
        borderLeft: `5px solid transparent`,
        borderRight: `5px solid transparent`,
        borderBottom: `15px solid ${entity.color}`,
        transform: `rotate(${entity.direction}rad)`,
        left: `${x - entity.radius}px`,
        bottom: `${y - entity.radius}px`,
    }

    console.log(entity.direction)

    return (
        <div className="entity planet" style={style}></div>
    )

}

export default EntityComponent
