import React from 'react'
import EntityDisplay from './EntityDisplay'
import { system } from '../classes/System'

const SystemDisplay = props => {

    const entity = {
        position: [75, 75],
        color: "blue",
        radius: 5,
    }

    const sun = {
        position: [140, 140],
        color: "yellow",
        radius: 15,
    }

    const entities = system.planets.map((planet, index) =>{
        <EntityDisplay entity={planet} />
    })

    return (
        <div className="space">
            {entities}
        </div>
    )
}

export default SystemDisplay
