class Planet {

    constructor(system, color, radius, x, y) {
        this.system = system
        this.color = color
        this.radius = radius
        this.style = new PlanetStyle(color, radius)
        this.position = [x, y]
        setInterval(() => {
            const [x, y] = this.position
            this.position = [x + 1, y + 1]
        }, 5000)
    }

    set position(position) {
        [this.x, this.y] = position
        this.style.position = position
    }

    get position() {
        return [this.x, this.y]
    }

}

export default Planet
