class Entity {

    constructor(system, color, radius, mass) {
        this.system = system
        this.mass = mass
        this.color = color
        this.radius = radius
        this.paths = []
    }

    position(time) {
        return this.path_value('position', time)
    }

    velocity(time) {
        return this.path_value('velocity', time)
    }

    speed(time) {
        return this.path_value('speed', time)
    }

    direction(time) {
        return this.path_value('direction', time)
    }

    path_value(value, time) {
        // console.log(value)
        const path = this.path(time)
        // console.log(path && path.direction(time))
        return path && path[value](time)
    }

    path(time) {
        for (const path of this.paths) {
            if (path.check_time(time)) {
                return path
            }
        }
    }

}

export default Entity
