import React, { useState, useEffect } from 'react'
import EntityDisplay from './EntityDisplay'
import System, { baseSystem } from '../classes/System'

const SystemDisplay = props => {

    const [system, setSystem] = useState(baseSystem)

    const [planets, setPlanets] = useState(baseSystem.planets)


    const entities = planets.map((planet, index) =>(
        <EntityDisplay key={index} entity={planet} />
    ))

    useEffect(() => {
        // setSystem(system => {
        //     system.new_planet('yellow', 9, 100, 100)
        //     system.new_planet('red', 5, 120, 80)
        //     console.log(system.planets)
        //     return system
        // })
        const interval = setInterval(() => {
            setSystem(system => system.step())
            setPlanets(system.planets)
            console.log(system.planets.map(planet => planet.position))
        },500)
        return () => clearInterval(interval)
    }, [])

    return (
        <div className="space">
            {entities}
        </div>
    )
}

export default SystemDisplay
