class Planet {

    constructor(system, color, radius, x, y) {
        this.system = system
        this.color = color
        this.radius = radius
        this.position = [x, y]
    }

    set position(position) {
        [this.x, this.y] = position
    }

    get position() {
        return [this.x, this.y]
    }

    step() {
        const steps = this.system.steps
        // console.log(steps)
        this.x = 140 + 100*Math.sin(2*Math.pi/1000*steps)
        this.y = 140 + 100*Math.cos(2*Math.pi/1000*steps)
    }

    style() {
        const dimensions = `${this.radius * 2}px`;
        const [x, y] = this.position
        return {
            height: dimensions,
            width: dimensions,
            backgroundColor: this.color,
            borderRadius: "50%",
            left: `${x - this.radius}px`,
            bottom: `${y - this.radius}px`,
        }
    }

}

export default Planet
