import React, { useState, useEffect } from 'react'
import EntityDisplay from './EntityDisplay'
import { baseSystem } from '../classes/System'

const SystemDisplay = props => {
    // const [value, setValue] = useState(0); // integer state
    const [system, setSystem] = useState(baseSystem)

    // const [planets, setPlanets] = useState(baseSystem.planets)

    const [planetStyles, setPlanetStyles] = useState(baseSystem.planets.map(planet => planet.style()))

    const styles = planetStyles.map((style, index) => (
        <EntityDisplay key={index} style={style} />
    ))

    useEffect(() => {
        // setSystem(system => {
        //     system.new_planet('yellow', 9, 100, 100)
        //     system.new_planet('red', 5, 120, 80)
        //     console.log(system.planets)
        //     return system
        // })
        const interval = setInterval(() => {
            console.log('interval')
            const nextSystem = system.step()
            const nextPlanetStyles = nextSystem.planets.map(planet => planet.style())
            setSystem(nextSystem)
            setPlanetStyles(nextPlanetStyles)
        }, 1000)
        return () => clearInterval(interval)
    }, [])


    // const intervalFunc = () => {
    //     setSystem(system => system.step())
    //     // setPlanets(system.planets)
    //     setValue(value + 1)
    // }

    // setInterval(intervalFunc,1000)

    return (
        <div className="space">
            {styles}
        </div>
    )
}

export default SystemDisplay
