import React, { useState, useEffect } from 'react'
import EntityComponent from './EntityComponent'
import { baseSystem } from '../classes/System'

const SystemComponent = ({ system }) => {

    const planetStyles = baseSystem.planets.map(planet => planet.style())

    const styles = planetStyles.map((style, index) => (
        <EntityComponent key={index} style={style} />
    ))

    return (
        <div className="space">
            {styles}
        </div>
    )
}

export default SystemComponent
