import Planet from './Planet'
import Orbit from './Orbit'

class System {

    constructor() {
        this.planets = []
        this.steps = 0
    }

    step() {
        this.steps++
        for (const planet of this.planets) {
            planet.color !== 'yellow' && planet.step() 
        }
        return this
    }

    new_planet(color, radius, x, y) {
        const planet = new Planet(this, color, radius, x, y)
        this.planets.push(planet)
        return planet
    }

}

export default System

const baseSystem = new System()

const sun = baseSystem.new_planet('yellow', 9, 140, 140)

const planet = baseSystem.new_planet('blue', 5)

const orbit = new Orbit(sun, 75, 500, 0, '+')

planet.orbit = orbit

const moon = baseSystem.new_planet('green', 2)

const moonOrbit = new Orbit(planet, 20, 400, 0, '+')

planet.orbit = orbit

export { baseSystem }
