import React from 'react'

const EntityComponent = ({ entity, zoom }) => {

    const dimensions = `${entity.radius * zoom * 2}px`;
    let [x, y] = entity.position
    x /= zoom
    y /= zoom

    console.log({zoom, x, y})

    const style = {
        height: dimensions,
        width: dimensions,
        backgroundColor: entity.color,
        borderRadius: "50%",
        left: `${x - entity.radius}px`,
        bottom: `${y - entity.radius}px`,
    }

    return (
        <div className="entity planet" style={style}></div>
    )

}

export default EntityComponent
