class Orbit {
    constructor(primary, radius, period, initialAngle, rotation = 0, direction = 1) {
        this.primary = primary
        this.radius = radius
        this.period = period
        this.initialAngle = initialAngle
        this.rotation = rotation
        this.direction = direction
    }

    position(steps) {
        const [minorRadius, majorRadius] = Array.isArray(this.radius) ? this.radius.sort() : [this.radius, this.radius]
        const [centreX, centreY] = this.getCentre(majorRadius, minorRadius)
        const angle = Math.PI * (this.direction * 2 / this.period * steps + this.initialAngle / 180);
        const [cosAngle, sinAngle] = [Math.cos(angle), Math.sin(angle)];
        const [cosRotation, sinRotation] = [Math.cos(this.rotation), Math.sin(this.rotation)]
        const term1 = majorRadius * cosAngle * cosRotation
        const term2 = minorRadius * sinAngle * sinRotation
        const x = term1 - term2 + centreX
        const y = term1 + term2 + centreY
        return [x, y]
    }

    // unrotated_position(steps) {
