    //     return [x, y]
    // }

    getCentre(majorRadius, minorRadius) {
        const [primaryX, primaryY] = Array.isArray(this.primary) ? this.primary : this.primary.position;
        return [primaryX + (majorRadius ** 2 - minorRadius ** 2) ** 0.5, primaryY]
    }
}

export default Orbit
