class Orbit {
    constructor(centre, radius, period, initial, direction = 1) {
        this.centre = centre
        this.radius = radius
        this.period = period
        this.initial = initial
        this.direction = direction
    }

    position(steps) {
        const [centreX, centreY] = Array.isArray(this.centre) ? this.centre : this.centre.position
        const angle = this.direction * 2 * Math.PI / this.period * (steps + this.initial)
        const x = centreX + this.radius * Math.cos(angle)
        const y = centreY + this.radius * Math.sin(angle)
        return [x, y]
    }
}

export default Orbit
