class Entity {

    constructor(system, color, radius, mass) {
        this.system = system
        this.mass = mass
        this.color = color
        this.radius = radius
    }

    position(time) {
        return this.path.position(time)
    }

    velocity(time) {
        return this.path.velocity(time)
    }

    speed(time) {
        return this.path.speed(time)
    }

    direction(time) {
        return this.path.direction(time)
    }

}

export default Entity
