import Planet from './Planet'
import Orbit from './Orbit'

class System {

    constructor() {
        this.planets = []
        this.steps = 0
    }

    step() {
        this.steps++
        for (const planet of this.planets) {
            planet.color !== 'yellow' && planet.step() 
        }
        return this
    }

    new_planet(color, radius, x, y) {
        const planet = new Planet(this, color, radius, x, y)
        this.planets.push(planet)
        return planet
    }

}

export default System

const baseSystem = new System()

const sun = baseSystem.new_planet('yellow', 5, 140, 140)

const planet = baseSystem.new_planet('blue', 2)
planet.orbit = new Orbit(sun, [75, 60], 500, 0, -1, -1)

const planet2 = baseSystem.new_planet('blue', 2)
planet2.orbit = new Orbit(sun, [75, 60], 500, 180, -1, 1)

const planet3 = baseSystem.new_planet('blue', 2)
planet3.orbit = new Orbit(sun, [60, 75], 500, 90, -1, -1)

const planet4 = baseSystem.new_planet('blue', 2)
planet4.orbit = new Orbit(sun, [60, 75], 500, 270, -1, 1)

const moon = baseSystem.new_planet('green', 1)

moon.orbit = new Orbit(planet, 10, 200, 0, -1)

const moon2 = baseSystem.new_planet('orange', 0.8)

moon2.orbit = new Orbit(planet, 20, 400, 0)

// const ast = baseSystem.new_planet('red', 0.5)

